	SwordRoomMapDataModel.prototype.getMapChunksAs2DArray = function(mapChunk)
	{
		var arr2d = [];
		var counter = 0;

		for(var i = 0; i < this.getMapChunkWidth(); i++)
		{
			arr2d[i] = [];
			for(var j = 0; j < this.getMapChunkHeight(); j++)
			{
				arr2d[i][j] = mapChunk[counter];
				counter++;
			}
		}
		return arr2d;
	};

